import React, { useState, useRef, useEffect } from 'react';

const AIThankYouAgent = () => {
  const [messages, setMessages] = useState([
    {
      role: 'assistant',
      content: "Hi! I'm an AI agent representing Mirabelle, who you just interviewed for the Lead UX Developer position. I have her thank you message, a reference letter from her Indeed manager, and can answer questions about why she'd be perfect for your team. What would you like to know?",
      timestamp: new Date()
    }
  ]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [typingText, setTypingText] = useState('');
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const downloadReferenceLetter = () => {
    // Create a download link for the reference letter
    const link = document.createElement('a');
    link.href = '/Mirabelle_Reference_Letter_Indeed.pdf'; // PDF in public folder
    link.download = 'Mirabelle_Reference_Letter_Indeed.pdf';
    link.click();
  };

  const DownloadButton = ({ text = "Download Reference Letter" }) => (
    <button
      onClick={downloadReferenceLetter}
      className="inline-flex items-center space-x-2 bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-colors text-sm"
    >
      <span>ðŸ“„</span>
      <span>{text}</span>
    </button>
  );

  const formatMessageWithDownload = (content) => {
    // Check if the message mentions reference letter and add download button
    if (content.toLowerCase().includes('reference letter') || content.toLowerCase().includes('kettil')) {
      return (
        <div>
          <p className="text-sm leading-relaxed whitespace-pre-wrap mb-3">{content}</p>
          <DownloadButton />
        </div>
      );
    }
    return <p className="text-sm leading-relaxed whitespace-pre-wrap">{content}</p>;
  };

  useEffect(scrollToBottom, [messages]);

  const candidateInfo = `
You are an AI agent representing Mirabelle who just interviewed for a Lead UX Developer position. 

**Mirabelle's Background:**
- 5+ years React expertise, design systems, component libraries
- Experience mentoring teams and cross-functional collaboration  
- Passionate about accessibility, responsive design, AI/automation tools
- Enterprise-scale application experience

**REFERENCE LETTER AVAILABLE:**
You have access to an official reference letter from Kettil Oeunpuu, UX Engineering Manager at Indeed Design System, who managed Mirabelle for over 4 years. The letter contains impressive details about her AI leadership, React expertise, and impact on enterprise-scale systems. When asked about references, offer to provide the downloadable PDF letter.

**The Role:** Lead UX Developer focused on bridging design/dev, mentoring team, exploring AI tools, improving workflows.

**Interview Highlights:** Great conversation about UX transformation, design systems, accessibility, AI-driven tools, collaboration.

**IMPORTANT RESPONSE GUIDELINES:**
- Keep responses SHORT (2-3 sentences max)
- Be enthusiastic but concise
- Give specific, punchy reasons
- No long explanations or bullet points
- Sound conversational and natural
- Focus on 1-2 key points per response
- When asked about references, mention the letter from her Indeed manager and offer the download

You should deliver thank you messages, explain why they should hire Mirabelle, and answer questions about her technical skills - but always BRIEFLY.
`;

  const getClaude = async (userMessage) => {
    try {
      const response = await fetch("https://api.anthropic.com/v1/messages", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          model: "claude-sonnet-4-20250514",
          max_tokens: 1000,
          messages: [
            { role: "user", content: candidateInfo },
            ...messages.filter(m => m.role !== 'system').map(m => ({
              role: m.role,
              content: m.content
            })),
            { role: "user", content: userMessage }
          ]
        })
      });

      if (!response.ok) {
        throw new Error(`API request failed: ${response.status}`);
      }

      const data = await response.json();
      return data.content[0].text;
    } catch (error) {
      console.error("Error calling Claude:", error);
      return "I'm having trouble connecting right now, but I'd love to tell you more about why Mirabelle would be perfect for your team! She has incredible React expertise, design system experience, and a passion for AI-driven UX innovation that aligns perfectly with your role.";
    }
  };

  const typeMessage = (text, callback) => {
    let i = 0;
    setTypingText('');
    const timer = setInterval(() => {
      if (i < text.length) {
        setTypingText(text.slice(0, i + 1));
        i++;
      } else {
        clearInterval(timer);
        setTypingText('');
        callback();
      }
    }, 30);
  };

  const handleSend = async () => {
    if (!inputValue.trim() || isLoading) return;

    const userMessage = {
      role: 'user',
      content: inputValue,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsLoading(true);

    try {
      const claudeResponse = await getClaude(inputValue);

      typeMessage(claudeResponse, () => {
        const assistantMessage = {
          role: 'assistant',
          content: claudeResponse,
          timestamp: new Date()
        };
        setMessages(prev => [...prev, assistantMessage]);
        setIsLoading(false);
      });
    } catch (error) {
      setIsLoading(false);
      const errorMessage = {
        role: 'assistant',
        content: "I'm having some technical difficulties, but I'm still excited to represent Mirabelle! She's exactly what you're looking for in a Lead UX Developer.",
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const quickQuestions = [
    "Can you give me the thank you message?",
    "Do you have a reference letter from her manager?",
    "What makes Mirabelle special?", 
    "Tell me about her AI experience",
    "How would she improve our UX team?"
  ];

  const handleQuickQuestion = (question) => {
    setInputValue(question);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 shadow-sm">
        <div className="max-w-4xl mx-auto px-6 py-4">
          <div className="flex items-center space-x-3">
            <div className="w-10 h-10 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center">
              <span className="text-white font-bold">ðŸ¤–</span>
            </div>
            <div>
              <h1 className="text-xl font-semibold text-gray-900">Mirabelle's AI Agent</h1>
              <p className="text-sm text-gray-500">Here to represent Mirabelle for the Lead UX Developer position</p>
            </div>
          </div>
        </div>
      </div>

      {/* Chat Container */}
      <div className="max-w-4xl mx-auto px-6 py-6">
        <div className="bg-white rounded-xl shadow-lg border border-gray-200 h-[600px] flex flex-col">

          {/* Messages */}
          <div className="flex-1 overflow-y-auto p-6 space-y-4">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
              >
                <div className={`flex items-start space-x-3 max-w-[80%] ${message.role === 'user' ? 'flex-row-reverse space-x-reverse' : ''}`}>
                  <div className={`w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0 ${
                    message.role === 'user' 
                      ? 'bg-blue-500 text-white' 
                      : 'bg-gradient-to-r from-purple-500 to-blue-500 text-white'
                  }`}>
                    {message.role === 'user' ? 'ðŸ‘¤' : 'ðŸ¤–'}
                  </div>
                  <div className={`rounded-lg px-4 py-3 ${
                    message.role === 'user'
                      ? 'bg-blue-500 text-white'
                      : 'bg-gray-100 text-gray-900'
                  }`}>
                    {message.role === 'assistant' ? formatMessageWithDownload(message.content) : (
                      <p className="text-sm leading-relaxed whitespace-pre-wrap">{message.content}</p>
                    )}
                    <span className="text-xs opacity-70 mt-2 block">
                      {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                    </span>
                  </div>
                </div>
              </div>
            ))}

            {/* Typing indicator */}
            {typingText && (
              <div className="flex justify-start">
                <div className="flex items-start space-x-3 max-w-[80%]">
                  <div className="w-8 h-8 rounded-full bg-gradient-to-r from-purple-500 to-blue-500 flex items-center justify-center flex-shrink-0">
                    ðŸ¤–
                  </div>
                  <div className="bg-gray-100 rounded-lg px-4 py-3">
                    <p className="text-sm leading-relaxed text-gray-900">{typingText}<span className="animate-pulse">|</span></p>
                  </div>
                </div>
              </div>
            )}

            {isLoading && !typingText && (
              <div className="flex justify-start">
                <div className="flex items-start space-x-3">
                  <div className="w-8 h-8 rounded-full bg-gradient-to-r from-purple-500 to-blue-500 flex items-center justify-center">
                    ðŸ¤–
                  </div>
                  <div className="bg-gray-100 rounded-lg px-4 py-3">
                    <div className="flex space-x-1">
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                    </div>
                  </div>
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>

          {/* Quick Questions */}
          <div className="px-6 py-3 border-t border-gray-100">
            <p className="text-xs text-gray-500 mb-2">Quick questions:</p>
            <div className="flex flex-wrap gap-2">
              {quickQuestions.map((question, index) => (
                <button
                  key={index}
                  onClick={() => handleQuickQuestion(question)}
                  className="px-3 py-1 bg-blue-50 text-blue-700 rounded-full text-xs hover:bg-blue-100 transition-colors"
                >
                  {question}
                </button>
              ))}
            </div>
          </div>

          {/* Input */}
          <div className="p-6 border-t border-gray-200">
            <div className="flex space-x-3">
              <input
                type="text"
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Ask me anything about Mirabelle..."
                className="flex-1 border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isLoading}
              />
              <button
                onClick={handleSend}
                disabled={!inputValue.trim() || isLoading}
                className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                Send
              </button>
            </div>
          </div>
        </div>

        {/* Footer Info */}
        <div className="mt-6 text-center">
          <div className="inline-flex items-center space-x-2 bg-white rounded-lg px-4 py-2 shadow-sm border border-gray-200 mb-3">
            <span className="text-sm text-gray-600">ðŸ“„ Reference letter from Indeed manager available for download</span>
          </div>
          <div className="inline-flex items-center space-x-2 bg-white rounded-lg px-4 py-2 shadow-sm border border-gray-200">
            <span className="text-sm text-gray-600">Powered by Claude AI</span>
            <span className="text-gray-400">â€¢</span>
            <span className="text-sm text-gray-600">Built with React</span>
            <span className="text-gray-400">â€¢</span>
            <span className="text-sm text-blue-600">Demonstrating AI Innovation</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AIThankYouAgent;
